import React, { useState } from 'react'
import { singleArray } from './singleArray'
import { Link } from 'react-router-dom'
const styles = {
    padding: "10px",
    margin: "5px",
    background: "gold",
    width: "100px",
    height: "100px",
    display: "inline-block"
};
function FilterPage() {

    let Data = singleArray;
    {
        menuItems.map((Val, id) => {
            return (
                <button
                    className="btn-dark text-white p-1 px-2 mx-5 btn fw-bold"
                    onClick={() => filterItem(Val)}
                    key={id}
                >
                    {Val}
                </button>
            );
        })
    }
    <button
        className="btn-dark text-white p-1 px-3 mx-5 fw-bold btn"
        onClick={() => setItem(Data)}
    >
        All
    </button>
    return (
        <React.Fragment>
            <ul className="list-unstyled templatemo-accordion cat_list " >
                <li className="pb-3" >
                    {/* <div style={{ margin: "10px" }}>
                        <button onClick={() => setFilter("all")}>All</button>
                        <button onClick={() => setFilter("Abdul bari")}>dog</button>
                        <button onClick={() => setFilter("Jann Cartoon")}>cat</button>
                        <button onClick={() => setFilter("Cartoon")}>lizard</button>
                    </div> */}
                </li>
            </ul>

            {
                Data.map((item) => {
                    return (
                        (
                            <div style={styles}>
                                <div className="col-md-4">
                                    <div className="card mb-4 product-wap rounded-0">
                                        <Link to={`/videodetail/${item.id}`}>
                                            <div className="card rounded-0">
                                                <img className="card-img rounded-0 img-fluid" src={item.img} alt="single -video" />
                                                <div className="card-img-overlay rounded-0 product-overlay d-flex align-items-center justify-content-center">
                                                </div>
                                            </div>
                                        </Link>
                                        <div className="card-body">
                                            <Link to={`/videodetail/${item.id}`} className="h3 text-decoration-none"><p>{item.title}</p></Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )
                    )
                })
            }


        </React.Fragment>
    )
}

export default FilterPage
