import React, { useState } from 'react'
import { singleArray } from './singleArray'
import { Link } from 'react-router-dom'
const styles = {
    padding: "10px",
    margin: "5px",
    background: "gold",
    width: "100px",
    height: "100px",
    display: "inline-block"
};
function FilterPage() {
    let Data = ["Abdul bari", "Jann Cartoon", "Cartoon", "ChuChu tv", "Minal Quran", "Tialwat Quran", "Jann Cartoon"];
    // let Data = singleArray;
    const [item, setItem] = useState(Data);

    const menuItems = [...new Set(Data.map((Val) => Val.category))];

    const filterItem = (curcat) => {
        const newItem = Data.filter((newVal) => {
            return newVal.category === curcat;
        });
        setItem(newItem);
    };
    const [filter, setFilter] = useState("all")
    const filterTags = (val) => {
        setFilter(val)
    }
    return (
        <React.Fragment>
            <ul className="list-unstyled templatemo-accordion cat_list " >
                <li className="pb-3" >
                    <div style={{ margin: "10px" }}>
                        <button onClick={() => setFilter("all")}>All</button>
                        <button onClick={() => setFilter("Abdul bari")}>dog</button>
                        <button onClick={() => setFilter("Jann Cartoon")}>cat</button>
                        <button onClick={() => setFilter("Cartoon")}>lizard</button>
                    </div>
                </li>
            </ul>
            <div filter={filter} setFilter={filterTags} ></div>
            {
                Data.map((item) => {
                    return (
                        filter === item || filter === "all" ? (
                            <div style={styles}>
                                {item}
                            </div>
                        ) : null
                    )
                })
            }


        </React.Fragment>
    )
}

export default FilterPage
