import YouTube from 'react-youtube';

let videoIdList = ["AOMpxsiUg2Q", "XM-HJT8_esM", "_nBlN9yp9R8", "Y-MX24vmECM"];


function TestPage() {
    const [videoId, setVideoId] = useState(videoIdList[0]);
    const [i, setIndex] = useState(0);

    const opts = {
        height: '390',
        width: '640',
        playerVars: {
            autoplay: 1,
        },
    };
    // const onReady = (event) => {
