import React, { useState } from 'react'
import student from "../../assets/images/student.png"
import Iframe from 'react-iframe'
import YouTubePlayer from 'react-youtube'
import { detailsArray } from './detailsArray'
import { Link } from 'react-router-dom'
import arrow from "../../assets/images/right-arrow.png"

export default function Detailspage() {
    // const details = detailsArray;

    // let [youtube, setYoutube] = useState('');
    let [video, setVideo] = useState('');

    let [current, setCurrent] = useState(0);
    let [videos, setVideos] = useState([

        {
            counter: 0,
            videoID: 'Y-MX24vmECM',
            videotitle: 'Make This Easy Braided Chicken Bread Without Oven',
        },
        {
            counter: 0,
            videoID: 'h_n4tDNY6SE',
            videotitle: '10 Minuets Cold Dessert | No Baking | No Oven',
        },
        {
            counter: 0,
            videoID: '1UM1oGnLsZE',
            videotitle: 'Restaurant Style Chicken Shashlik with Gravy Recipe',
        },
        {
            counter: 0,
            videoID: 'YbBQrjKBOXQ',
            videotitle: 'Crispy Chicken Cheese Patty Burger Recipe',
        },
    ]);


    const handlePrev = () => {
        console.log("pre button")
        setCurrent((index) => index === videos.length - 1 ? 0 : index - 1);
    }
    const handleNext = () => {
        console.log("next button")
        setCurrent((index) => index === videos.length - 1 ? 0 : index + 1);
    }
    return (
        <React.Fragment>
            <section className='py-5 mb-5'>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-7 ">
                            <h2 className="main-heading  ">
                                Video Title
                            </h2>
<>
    
</>
                            <YouTubePlayer
                                videoId={video}
                            />

                            <div className='d-flex justify-content-center'>
                                <div className="d-flex justify-content-center mt-3" onClick={handlePrev}>
                                    <Link to="#" className="call_to-btn " >
                                        <span>
                                            Prev
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                                <div className="d-flex justify-content-center mt-3 ms-2" onClick={handleNext}>
                                    <Link to="#" className="call_to-btn  ">
                                        <span>
                                            Next
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                            </div>

                            {/* display with Iframe  */}



                        </div>
                        <div className="col-lg-5 mt-5">

                            {
                                videos.map((item) => {
                                    return (
                                        <div className="card d-flex m-2" style={{ flexDirection: "row" }} onClick={() => {
                                            setVideo(item.videoID)
                                        }}>
                                            <img className="w-25" src={"http://img.youtube.com/vi/" + item.videoID + "/0.jpg"} />
                                            <p className='p-2'>{item.videotitle}</p>
                                        </div>
                                    )
                                })
                            }

                        </div>
                    </div>
                </div>

            </section >
        </React.Fragment >
    )
}
