import React, { useRef, useState } from 'react'
import Iframe from 'react-iframe'
import YouTubePlayer from 'react-youtube'
export default function Detailspage() {
    // const details = detailsArray;

    // let [youtube, setYoutube] = useState('');
    const frame = 25
    const vRef = useRef();
    const [currentFrame, setCurrentFrame] = useState(0);
    let [video, setVideo] = useState('');
    const [videoDur, setvideoTotalDuration] = useState(0);
    const [curTime, setVideoCurrentTime] = useState(0);
    const onTimeUpdate = () => {
        let ref = vRef.current();
        if (ref) {
            let videoTotalDuration = ref.duration;
            let videoDuration = ref.currentTime;
            let videoCurrentFrame = Math.floor(ref.currentTime * frames);
            setCurrentFrame(videoCurrentFrame);
            setvideoTotalDuration(videoTotalDuration);
            setVideoCurrentTime(videoDuration);
        }
    }

    let [videos, setVideos] = useState([

        {
            counter: 0,
            videoID: 'Y-MX24vmECM',
            videotitle: 'Make This Easy Braided Chicken Bread Without Oven',
        },
        {
            counter: 0,
            videoID: 'h_n4tDNY6SE',
            videotitle: '10 Minuets Cold Dessert | No Baking | No Oven',
        },
        {
            counter: 0,
            videoID: '1UM1oGnLsZE',
            videotitle: 'Restaurant Style Chicken Shashlik with Gravy Recipe',
        },
        {
            counter: 0,
            videoID: 'YbBQrjKBOXQ',
            videotitle: 'Crispy Chicken Cheese Patty Burger Recipe',
        },
    ]);

    return (
        <React.Fragment>
            <section className='py-5 mb-5'>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-7 ">
                            <h2 className="main-heading  ">
                                Video Title
                            </h2>
                            <div className='mt-3' onTimeUpdate={onTimeUpdate}>
                                <iframe width="560" height="315" src="https://www.youtube.com/embed/GxeF44r30F8?rel=0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
                                {/* <YouTubePlayer
                                    videoId={video}
                                /> */}

                            </div >
                            <div>
                                <p>        {curTime}/{videoDur}
                                </p>
                                {currentFrame}
                            </div>


                        </div>
                        <div className="col-lg-5 mt-5">

                            {
                                videos.map((item) => {
                                    return (
                                        <div className="card d-flex m-2" style={{ flexDirection: "row" }} onClick={() => {
                                            setVideo(item.videoID)
                                        }}>
                                            <img className="w-25" src={"http://img.youtube.com/vi/" + item.videoID + "/0.jpg"} />
                                            <p className='p-2'>{item.videotitle}</p>
                                        </div>
                                    )
                                })
                            }

                        </div>
                    </div>
                </div>

            </section >
        </React.Fragment >
    )
}
