import React, { useState } from 'react'
import data from "./data.json"
import { Link } from 'react-router-dom'
import YouTube from 'react-youtube';
import YouTubePlayer from 'react-youtube';

function TestPage() {
    let [video, setVideo] = useState('');
    let [current,setCurrent] = useState(0);
    let [videos, setVideos] = useState([

        {
            counter: 0,
            videoID: 'Y-MX24vmECM',
            videotitle: 'Make This Easy Braided Chicken Bread Without Oven',
        },
        {
            counter: 0,
            videoID: 'h_n4tDNY6SE',
            videotitle: '10 Minuets Cold Dessert | No Baking | No Oven',
        },
        {
            counter: 0,
            videoID: '1UM1oGnLsZE',
            videotitle: 'Restaurant Style Chicken Shashlik with Gravy Recipe',
        },
        {
            counter: 0,
            videoID: 'YbBQrjKBOXQ',
            videotitle: 'Crispy Chicken Cheese Patty Burger Recipe',
        },
    ]);

    const handlePre=()=>{
        setCurrent((index) => index === video.length - 1 ? 0 : index - 1);
    }

    return (
        <div>
            <YouTubePlayer
                videoId={video}
            />
            <button onClick={handlePre}>
                pre
            </button>
            <button onClick={handleNext}>
                pre
            </button>
            {
                videos.map((item) => {
                    return (
                        <div className="videoCard" onClick={() => {
                            // setVideo([...video])
                            setVideo(item.videoID)
                        }}>
                            <img src={"http://img.youtube.com/vi/" + item.videoID + "/0.jpg"} />
                        </div>
                    )
                })
            }

        </div>
    )
}

export default TestPage
