import YouTube from 'react-youtube';

let videoIdList = ["_nBlN9yp9R8", "Y-MX24vmECM", "1UM1oGnLsZE"];

function TestPage() {
    const [videoId, setVideoId] = useState(videoIdList[0]);
    let i = 0;
    const opts = {
        height: '390',
        width: '640',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 1,
        },
    };
    // const onReady = (event) => {
