import React, { useState } from 'react'
import student from "../../assets/images/student.png"
import Iframe from 'react-iframe'
import YouTubePlayer from 'react-youtube'
import { detailsArray } from './detailsArray'
import { Link } from 'react-router-dom'
import arrow from "../../assets/images/right-arrow.png"

export default function Detailspage() {
    // const details = detailsArray;

    // let [youtube, setYoutube] = useState('');

    let [current, setCurrent] = useState(0);
    let [video ,setVideo] =useState([
video
    ])

    const option = {
        height: '100',
        width: '200',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 1,
        },
    };

    const handlePrev = () => {
        console.log("pre button")
        setCurrent((index) => index === video.length - 1 ? 0 : index - 1);
    }
    const handleNext = () => {
        console.log("next button")
        setCurrent((index) => index === video.length - 1 ? 0 : index + 1);
    }
    return (
        <React.Fragment>
            <section className='py-5 mb-5'>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-7 mt-3 ">
                            <h2 className="main-heading  ">
                                Video Title
                            </h2>
                            {/* Display with youtube player  */}
                            {/* <YouTubePlayer videoId={video[current]} /> */}

                            {/* demo work  */}
                            {/* {
                                video.map((item) => {
                                    return (
                                        <div onClick={() => {
                                            setYoutube
                                                (item.videoID)
                                        }} >

                                        </div>
                                    )
                                })
                            } */}
                            <div className='d-flex justify-content-center'>
                                <div className="d-flex justify-content-center mt-3" onClick={handlePrev}>
                                    <Link to="#" className="call_to-btn " >
                                        <span>
                                            Prev
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                                <div className="d-flex justify-content-center mt-3 ms-2" onClick={handleNext}>
                                    <Link to="#" className="call_to-btn  ">
                                        <span>
                                            Next
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                            </div>

                            {/* display with Iframe  */}
                            {
                                video.map((item) => {
                                    return (
                                        <Iframe src={`https://www.youtube.com/embed/${item.videoID}`}
                                            //  'https://www.youtube.com/embed/' + id + '?showinfo=0&enablejsapi=1&origin=http://localhost:3000';


                                            allowfullscreen
                                            width="560" height="315"
                                            // width="675px"
                                            // height="320px"
                                            id=""
                                            className="mb-3"
                                            display="block"
                                            position="relative"

                                        />
                                    )
                                })
                            }


                        </div>
                        <div className="col-lg-5 mt-5">

                            {
                                video.map((item) => {
                                    const { title, img } = item
                                    return (
                                        <div className="card m-2">
                                            <div class="card-body d-flex">
                                                {/* <img src={img} alt="student" className="w-75" /> */}
                                                {/* <YouTubePlayer videoId={video[current]} opts={option} /> */}

                                                <img src={"http://img.youtube.com/vi/" + `${video[current]}` + "/0.jpg"} className='w-25' />
                                                {/* <video controls className='w-25'>
                                                    <source src={`https://www.youtube.com/embed/${item.videoID}`} />
                                                </video> */}

                                                <span className='title ms-3'>{title}</span>

                                            </div>
                                        </div>
                                    )
                                })
                            }


                        </div>
                    </div>
                </div>

            </section >
        </React.Fragment >
    )
}
