import { useState } from "react";
import YouTubePlayer from 'react-youtube';


export default function TestPage() {

    let [video, setVideo] = useState('');
    let [videos, setVideos] = useState([

        {
            counter: 0,
            videoID: 'Y-MX24vmECM',
            videotitle: 'Make This Easy Braided Chicken Bread Without Oven',
        },
        {
            counter: 0,
            videoID: 'h_n4tDNY6SE',
            videotitle: '10 Minuets Cold Dessert | No Baking | No Oven',
        },
        {
            counter: 0,
            videoID: '1UM1oGnLsZE',
            videotitle: 'Restaurant Style Chicken Shashlik with Gravy Recipe',
        },
        {
            counter: 0,
            videoID: 'YbBQrjKBOXQ',
            videotitle: 'Crispy Chicken Cheese Patty Burger Recipe',
        },
    ]);

    return <div>
        <YouTubePlayer videoId={video} />
        <div className="video-container">
            {
                videos.map((video) => {
                    return <div className="videoCard" onClick={() => {
                        video.counter++;
                        // setVideo([...video])
                        setVideo(video.videoID)
                    }}>
                        <img src={"http://img.youtube.com/vi/" + video.videoID + "/0.jpg"} />
                        <h4>{video.videotitle}</h4>
                        <span className="videoCounter">{video.counter}</span>
                    </div>
                })
            }
        </div>
    </div>
}
