import React, { useEffect, useState } from 'react'
import YouTube from 'react-youtube';

function TestPage() {
    let videoIdList = ["AOMpxsiUg2Q", "XM-HJT8_esM", "AOMpxsiUg2Q"];
    let [youtube ,setYoutube] = useState(videoIdList[0])
    let i = 0;
    const opts = {
        height: '390',
        width: '640',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 1,
        },
    };
    // const onReady = (event) => {
    //     // access to player in all event handlers via event.target
    //     event.target.pauseVideo();
    //     console.log(event.target)
    // }


    // const onPlayVideo = (event) => {
    //     const player = event.target;
    //     console.log(player.getCurrentTime())
    //     const time = player.getCurrentTime();
    //     if (time > 23.84825304196167) {
    //         player.stopVideo();
    //         alert("video is end")
    //     }
    // }
    useEffect(()=>{
        setYoutube(videoIdList[i])
    })
    const onEndVideo = () => {
       i++;
       setYoutube([videoIdList[i]])
       
    }

    return (
        <div>
            <YouTube videoId={videoIdList[i]} opts={opts} onEnd={onEndVideo} />;

        </div>
    )
}

export default TestPage
