import React, { useRef, useState } from 'react'
import YouTube from '@u-wave/react-youtube';
// let videoIdList = ["AOMpxsiUg2Q", "XM-HJT8_esM", "r2pdwduPS5U"];

export default function Detailspage() {
    const [videoId, setVideoId] = useState(
        
    )
    const videoIdList=[
        {
            id:"AOMpxsiUg2Q",
            title:"video of sommaife"
        },
        {
            id:"XM-HJT8_esM",
            title:"video of sommaife"
        },
        {
            id:"r2pdwduPS5U",
            title:"video of sommaife"
        }
    ]
    const a = useRef(0);
    // const option = {
    //     height: '390',
    //     width: '640',
    //     playerVars: {
    //         autoplay: 1,
    //     },
    // };

    const handleEnd = () => {
        setVideoId(videoIdList[++a.current]);
    };


    const onReady = (event) => {
        // access to player in all event handlers via event.target
        event.target.pauseVideo();
    }
    return (
        <React.Fragment>
            <section className=' layout_padding' >
                <div className="container">
                    <div className="row">
                        <h2 className="main-heading  ">
                            Video Title
                        </h2>
                        <div className="col-lg-7 mt-5 ">

                            <YouTube video={videoIdList.id}
                                width={600}
                                height={400}
                                onEnd={handleEnd}
                                onReady={onReady}
                                autoplay
                                // controls={false}
                            />
                        </div>
                        <div className="col-lg-5 mt-5">
                            {
                                videoIdList.map((item) => {
                                    return (
                                        <div className="card d-flex m-2" style={{ flexDirection: "row" }} onClick={() => {
                                            setVideoId(item.videoIdList)
                                        }}>
                                            <img className="w-25" src={"http://img.youtube.com/vi/" + item.videoId + "/0.jpg"} />
                                            <p className='p-2'>{item.videotitle}</p>
                                        </div>
                                    )
                                })
                            }

                        </div>
                    </div>
                </div>

            </section >
        </React.Fragment >
    )
}
