import React, { useState } from 'react'

import student from "../../assets/images/student.png"
import Iframe from 'react-iframe'
import YouTubePlayer from 'react-youtube'

import { detailsArray } from './detailsArray'
import { Link } from 'react-router-dom'
import arrow from "../../assets/images/right-arrow.png"

export default function Detailspage() {
    const details = detailsArray;

    let [youtube, setYoutube] = useState('');
    let [current ,setCurrent] = useState(0);
    let video = [
        'zZ1inK94DqU',
        "onZfUE6JWr",
        'zZ1inK94DqU',

    ]
    const option = {
        height: '560',
        width: '315',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 1,
        },
    };

    const handlePrev =()=>{
    
    }
    return (
        <React.Fragment>
            <section className='py-5 mb-5'>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-7 mt-3 ">
                            <h2 className="main-heading  ">
                                Video Title
                            </h2>
                            {/* Display with youtube player  */}
                            <YouTubePlayer videoId={video} />


                            {
                                video.map((item) => {
                                    return (
                                        <div onClick={() => {
                                            setYoutube
                                                (item.videoID)
                                        }} >

                                        </div>
                                    )
                                })
                            }
                            <div className='d-flex justify-content-center'>
                                <div className="d-flex justify-content-center mt-3" onClick={handlePrev}>
                                    <Link to="#" className="call_to-btn " onClick={handleNext}>
                                        <span>
                                            Prev
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                                <div className="d-flex justify-content-center mt-3 ms-2">
                                    <Link to="#" className="call_to-btn  ">
                                        <span>
                                            Next
                                        </span>
                                        <img src={arrow} alt="arrow" />
                                    </Link>
                                </div>
                            </div>

                            {/* display with Iframe  */}
                            {/* {
                                video.map((item) => {
                                    return (
                                        <Iframe src={`https://www.youtube.com/embed/${item.videoID}`} allowfullscreen
                                            width="560" height="315"
                                            // width="675px"
                                            // height="320px"
                                            id=""
                                            className="mb-3"
                                            display="block"
                                            position="relative"

                                        />
                                    )
                                })
                            } */}


                        </div>
                        <div className="col-lg-5 mt-5">

                            {
                                video.map((item) => {
                                    const { title, img } = item
                                    return (
                                        <div className="card m-2">
                                            <div class="card-body d-flex">
                                                {/* <img src={img} alt="student" className="w-75" /> */}
                                                <img src={"http://img.youtube.com/vi/" + item.videoID + "/0.jpg"} className='w-25' />
                                                {/* <video controls className='w-25'>
                                                    <source src={`https://www.youtube.com/embed/${item.videoID}`} />
                                                </video> */}

                                                <span className='title ms-3'>{title}</span>

                                            </div>
                                        </div>
                                    )
                                })
                            }


                        </div>
                    </div>
                </div>

            </section>
        </React.Fragment>
    )
}
